* SecurityConfig
```java
@Configuration
@EnableWebSecurity
@RequiredArgsConstructor
public class SecurityConfig extends WebSecurityConfigurerAdapter {

    private final UserDetailsService userDetailsService;



    /**
     *
     * \xec\x8a\xa4\xed\x94\x84\xeb\xa7\x81 \xec\x8b\x9c\xed\x81\x90\xeb\xa6\xac\xed\x8b\xb0 \xeb\xa3\xb0\xec\x9d\x84 \xeb\xac\xb4\xec\x8b\x9c\xed\x95\xa0 URL \xea\xb7\x9c\xec\xb9\x99 \xec\x84\xa4\xec\xa0\x95
     * \xec\xa0\x95\xec\xa0\x81 \xec\x9e\x90\xec\x9b\x90\xec\x97\x90 \xeb\x8c\x80\xed\x95\xb4\xec\x84\x9c\xeb\x8a\x94 Security \xec\x84\xa4\xec\xa0\x95\xec\x9d\x84 \xec\xa0\x81\xec\x9a\xa9\xed\x95\x98\xec\xa7\x80 \xec\x95\x8a\xec\x9d\x8c
     */
    @Override
    public void configure(WebSecurity web) throws Exception {
        web.ignoring()
                .antMatchers("/css/**", "/js/**");


    }

    @Override
    protected void configure(HttpSecurity http) throws Exception {
        http.csrf().disable();
        http.authorizeRequests()
                .antMatchers("/", "/members/new", "/items", "/login/**", "/v3/api-docs",
                        "/swagger*/**").permitAll() //\xec\x9d\xb8\xec\xa6\x9d\xec\x9d\xb4 \xec\x97\x86\xec\x96\xb4\xeb\x8f\x84 \xec\xa0\x91\xea\xb7\xbc \xea\xb0\x80\xeb\x8a\xa5
                .anyRequest().authenticated()
                .and()
                .formLogin()
                .loginPage("/login")
                .successHandler(customLoginSuccessHandler())
                .failureUrl("/login/error")
                .failureHandler(failureHandler())
                .and()
                .logout()
                .logoutUrl("/logout");

        //postman \xec\x84\xa4\xec\xa0\x95  (\xed\x8f\xac\xec\x8a\xa4\xed\x8a\xb8\xeb\xa7\xa8\xec\x97\x90\xeb\x8f\x84 authorization \xed\x95\xb4\xec\xa4\x98\xec\x95\xbc \xed\x95\xa8)
        http.httpBasic().and();
        //cors \xec\x84\xa4\xec\xa0\x95 (\xed\x85\x8c\xec\x8a\xa4\xed\x8a\xb8 \xed\x95\xb4\xeb\xb3\xb4\xec\xa7\x84 \xec\x95\x8a\xec\x9d\x8c)
        http.authorizeRequests().and().cors();
    }

    @Bean
    public BCryptPasswordEncoder bCryptPasswordEncoder() {
        // \xeb\xb9\x84\xeb\xb0\x80\xeb\xb2\x88\xed\x98\xb8 \xec\x95\x94\xed\x98\xb8\xed\x99\x94 \xed\x95\xa0\xeb\x95\x8c \xec\x82\xac\xec\x9a\xa9\xed\x95\xa0 BCrypthPasswordEncoder \xeb\xa5\xbc \xeb\xb9\x88\xec\x9c\xbc\xeb\xa1\x9c \xeb\x93\xb1\xeb\xa1\x9d
        return new BCryptPasswordEncoder();
    }


    @Bean
    public CustomLoginSuccessHandler customLoginSuccessHandler() {
        // \xec\x84\xb1\xea\xb3\xb5\xed\x95\xa0 \xeb\x95\x8c \xec\x8b\xa4\xed\x96\x89\xeb\x90\x98\xec\x96\xb4\xec\x95\xbc \xed\x95\x98\xeb\x8a\x94 CustomLoginSuccessHandler \xeb\xa5\xbc \xeb\xb9\x88\xec\x9c\xbc\xeb\xa1\x9c \xeb\x93\xb1\xeb\xa1\x9d
        return new CustomLoginSuccessHandler();
    }

    @Bean
    public AuthenticationFailureHandler failureHandler() {
        return new CustomAuthFailureHandler();
    }

    /**
     * cors \xec\x97\x90\xeb\x9f\xac \xed\x95\xb4\xea\xb2\xb0 (\xec\x8a\xa4\xed\x94\x84\xeb\xa7\x81 \xec\x8b\x9c\xed\x81\x90\xeb\xa6\xac\xed\x8b\xb0\xec\x97\x90\xec\x84\x9c)
     */
    @Bean
    public CorsConfigurationSource corsConfigurationSource() {
        CorsConfiguration configuration = new CorsConfiguration();

        configuration.addAllowedOriginPattern("*");
        configuration.addAllowedHeader("*");
        configuration.addAllowedMethod("*");
        configuration.setAllowCredentials(true);

        UrlBasedCorsConfigurationSource source = new UrlBasedCorsConfigurationSource();
        source.registerCorsConfiguration("/**", configuration);
        return source;
    }

//    @Bean
//    public CustomAuthenticationProvider customAuthenticationProvider() {
//        // \xec\x8b\xa4\xec\xa0\x9c \xec\x9d\xb8\xec\xa6\x9d \xeb\x8b\xb9\xeb\x8b\xb4 \xea\xb0\x9d\xec\xb2\xb4\xeb\xa5\xbc \xeb\xb9\x88\xec\x9c\xbc\xeb\xa1\x9c \xeb\x93\xb1\xeb\xa1\x9d
//        return new CustomAuthenticationProvider(userDetailsService, bCryptPasswordEncoder());
//    }

    @Override
    public void configure(AuthenticationManagerBuilder authenticationManagerBuilder) throws Exception {
        //AuthenticationManager \xec\x97\x90 \xeb\x93\xb1\xeb\xa1\x9d
        authenticationManagerBuilder.userDetailsService(userDetailsService).passwordEncoder(bCryptPasswordEncoder());
    }
}
```

* CustomAuthenticationProvider  (\xec\x9d\xb4\xea\xb1\xb0 \xec\x97\x86\xec\x96\xb4\xeb\x8f\x84 \xeb\x8f\x99\xec\x9e\x91 \xed\x95\xa8. \xeb\x82\x98\xec\xa4\x91\xec\x97\x90 \xec\xbb\xa4\xec\x8a\xa4\xed\x85\x80 \xed\x95\x84\xec\x9a\x94\xed\x95\x98\xeb\xa9\xb4 \xea\xb3\xb5\xeb\xb6\x80)
```java
@Slf4j
@RequiredArgsConstructor
public class CustomAuthenticationProvider implements AuthenticationProvider {

    private final UserDetailsService userDetailsService;
    private final BCryptPasswordEncoder passwordEncoder;
    @Override
    public Authentication authenticate(Authentication authentication) throws AuthenticationException {
        //DB\xec\x97\x90\xec\x84\x9c \xec\x82\xac\xec\x9a\xa9\xec\x9e\x90 \xec\xa0\x95\xeb\xb3\xb4\xea\xb0\x80 \xec\x8b\xa4\xec\xa0\x9c\xeb\xa1\x9c \xec\x9c\xa0\xed\x9a\xa8\xed\x95\x9c\xec\xa7\x80 \xed\x99\x95\xec\x9d\xb8 \xed\x9b\x84 \xec\x9d\xb8\xec\xa6\x9d\xeb\x90\x9c Authentication \xeb\xa6\xac\xed\x84\xb4
        UsernamePasswordAuthenticationToken token = (UsernamePasswordAuthenticationToken) authentication; //\xec\x83\x9d\xec\x84\xb1\xeb\x90\x9c \xed\x86\xa0\xed\x81\xb0\xec\x9c\xbc\xeb\xa1\x9c\xeb\xb6\x80\xed\x84\xb0 \xec\x95\x84\xec\x9d\xb4\xeb\x94\x94\xec\x99\x80 \xeb\xb9\x84\xeb\xb0\x80\xeb\xb2\x88\xed\x98\xb8 \xec\xa1\xb0\xed\x9a\x8c
        String userName = token.getName();
        String userPassword = (String) token.getCredentials(); //UserDetailsService\xeb\xa5\xbc \xed\x86\xb5\xed\x95\xb4 \xec\x95\x84\xec\x9d\xb4\xeb\x94\x94\xeb\xa1\x9c \xec\x82\xac\xec\x9a\xa9\xec\x9e\x90 \xec\xa1\xb0\xed\x9a\x8c...?? \xec\x97\xac\xea\xb8\xb0 \xeb\xad\x90\xec\xa7\x80

        System.out.println(userName);
        System.out.println(userPassword);

        Member member = (Member) userDetailsService.loadUserByUsername(userName); //\xec\x9d\xb4\xea\xb1\xb8 \xec\x98\xa4\xeb\xb2\x84\xeb\x9d\xbc\xec\x9d\xb4\xeb\x93\x9c \xed\x95\xb4\xec\x95\xbc\xed\x95\x98\xeb\x82\x98\xeb\xb4\x84

        if(passwordEncoder.matches(userPassword, member.getPassword()) == false) {
            throw new BadCredentialsException(member.getName() + "  \xeb\xb9\x84\xeb\xb0\x80\xeb\xb2\x88\xed\x98\xb8\xeb\xa5\xbc \xed\x99\x95\xec\x9d\xb8\xed\x95\xb4\xec\xa3\xbc\xec\x84\xb8\xec\x9a\x94");
        }
        return new UsernamePasswordAuthenticationToken(member, userPassword, member.getAuthorities());
    }

    @Override
    public boolean supports(Class<?> authentication) {
        return authentication.equals(UsernamePasswordAuthenticationToken.class);
    }
}
