* CustomAuthFailureHandler
```java
@Component
public class CustomAuthFailureHandler extends SimpleUrlAuthenticationFailureHandler {

    private final String DEFAULT_FAILURE_URL = "/login/error";

    @Override
    public void onAuthenticationFailure(HttpServletRequest request, HttpServletResponse response, AuthenticationException exception) throws IOException, ServletException {
        String errorMessage = null;

        //=================================================
        //< set the error message
        //=================================================
        //< incorrect the identify or password
        if(exception instanceof BadCredentialsException || exception instanceof InternalAuthenticationServiceException) {
            errorMessage = "\xec\x95\x84\xec\x9d\xb4\xeb\x94\x94\xeb\x82\x98 \xeb\xb9\x84\xeb\xb0\x80\xeb\xb2\x88\xed\x98\xb8\xea\xb0\x80 \xeb\xa7\x9e\xec\xa7\x80 \xec\x95\x8a\xec\x8a\xb5\xeb\x8b\x88\xeb\x8b\xa4. \xeb\x8b\xa4\xec\x8b\x9c \xed\x99\x95\xec\x9d\xb8\xed\x95\xb4 \xec\xa3\xbc\xec\x8b\xad\xec\x8b\x9c\xec\x98\xa4.";
        }
        //< account is disabled
        else if(exception instanceof DisabledException) {
            errorMessage = "\xea\xb3\x84\xec\xa0\x95\xec\x9d\xb4 \xeb\xb9\x84\xed\x99\x9c\xec\x84\xb1\xed\x99\x94 \xeb\x90\x98\xec\x97\x88\xec\x8a\xb5\xeb\x8b\x88\xeb\x8b\xa4. \xea\xb4\x80\xeb\xa6\xac\xec\x9e\x90\xec\x97\x90\xea\xb2\x8c \xeb\xac\xb8\xec\x9d\x98\xed\x95\x98\xec\x84\xb8\xec\x9a\x94.";
        }
        //< expired the credential
        else if(exception instanceof CredentialsExpiredException) {
            errorMessage = "\xeb\xb9\x84\xeb\xb0\x80\xeb\xb2\x88\xed\x98\xb8 \xec\x9c\xa0\xed\x9a\xa8\xea\xb8\xb0\xea\xb0\x84\xec\x9d\xb4 \xeb\xa7\x8c\xeb\xa3\x8c \xeb\x90\x98\xec\x97\x88\xec\x8a\xb5\xeb\x8b\x88\xeb\x8b\xa4. \xea\xb4\x80\xeb\xa6\xac\xec\x9e\x90\xec\x97\x90\xea\xb2\x8c \xeb\xac\xb8\xec\x9d\x98\xed\x95\x98\xec\x84\xb8\xec\x9a\x94.";
        }
        else {
            errorMessage = "\xec\x95\x8c\xec\x88\x98 \xec\x97\x86\xeb\x8a\x94 \xec\x9d\xb4\xec\x9c\xa0\xeb\xa1\x9c \xeb\xa1\x9c\xea\xb7\xb8\xec\x9d\xb8\xec\x97\x90 \xec\x8b\xa4\xed\x8c\xa8\xed\x95\x98\xec\x98\x80\xec\x8a\xb5\xeb\x8b\x88\xeb\x8b\xa4. \xea\xb4\x80\xeb\xa6\xac\xec\x9e\x90\xec\x97\x90\xea\xb2\x8c \xeb\xac\xb8\xec\x9d\x98\xed\x95\x98\xec\x84\xb8\xec\x9a\x94.";
        }

        // UTF-8 \xec\x9d\xb8\xec\xbd\x94\xeb\x94\xa9 \xec\xb2\x98\xeb\xa6\xac
        errorMessage = URLEncoder.encode(errorMessage, "UTF-8");
        setDefaultFailureUrl("/login/error?msg=" + errorMessage);

        super.onAuthenticationFailure(request, response, exception);
    }
}
