@Service
@RequiredArgsConstructor
public class UserDetailsServiceImpl implements UserDetailsService {

    private final MemberRepository memberRepository;


    @Override
        public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {
            // \xec\x82\xac\xec\x9a\xa9\xec\x9e\x90 \xec\x9d\xb4\xeb\xa6\x84\xec\x9d\x84 \xea\xb8\xb0\xeb\xb0\x98\xec\x9c\xbc\xeb\xa1\x9c \xec\x82\xac\xec\x9a\xa9\xec\x9e\x90 \xec\xa0\x95\xeb\xb3\xb4\xeb\xa5\xbc \xea\xb0\x80\xec\xa0\xb8\xec\x98\xb5\xeb\x8b\x88\xeb\x8b\xa4.
            Member member = memberRepository.findByNamed(username);
            if(member == null) {
                System.out.println("member=null");
                throw new UsernameNotFoundException("username\xec\x97\x90 \xed\x95\xb4\xeb\x8b\xb9\xed\x95\x98\xeb\x8a\x94 Entity \xec\x97\x86\xec\x9d\x8c");
            }
            // Spring Security\xea\xb0\x80 \xec\x82\xac\xec\x9a\xa9\xed\x95\xa0 UserDetails \xea\xb0\x9d\xec\xb2\xb4\xeb\xa5\xbc \xeb\xa7\x8c\xeb\x93\xad\xeb\x8b\x88\xeb\x8b\xa4.
            return User.builder().username(member.getName()).password(member.getPassword()).roles("user").build();
        }
}
